import React, { useState } from 'react';
import { updateBuilding, deleteBuilding } from '../services/api';
import { Building } from '../types/types';
import '../styles.css'; // Ensure global styles are applied

interface BuildingListProps {
  buildings: Building[];
  setBuildings: React.Dispatch<React.SetStateAction<Building[]>>;
  onEdit: (building: Building) => void;
}

const BuildingList: React.FC<BuildingListProps> = ({ buildings, setBuildings, onEdit }) => {
  const [temperatureInputs, setTemperatureInputs] = useState<{ [key: number]: string }>({});

  const handleDelete = async (id: number) => {
    await deleteBuilding(id);
    setBuildings(buildings.filter((b) => b.id !== id));
  };

  const handleStatusUpdate = async (building: Building, status: string) => {
    const updatedBuilding = { ...building, status };
    await updateBuilding(building.id, updatedBuilding);
    setBuildings(buildings.map((b) => (b.id === building.id ? updatedBuilding : b)));
  };

  const handleTemperatureUpdate = async (building: Building) => {
    const newTemperature = parseFloat(temperatureInputs[building.id] || '');
    if (!isNaN(newTemperature)) {
      const updatedBuilding = { ...building, temperature: newTemperature };
      await updateBuilding(building.id, updatedBuilding);
      setBuildings(buildings.map((b) => (b.id === building.id ? updatedBuilding : b)));
      setTemperatureInputs((prev) => ({ ...prev, [building.id]: '' })); // Reset input field
    }
  };

  return (
    <div className="building-list">
      {buildings.map((building) => (
        <div key={building.id} className="building-card">
          <h3>{building.name}</h3>
          <p>Location: {building.location}</p>
          <p>Status: {building.status}</p>
          <p>Current Temperature: {building.temperature}Â°C</p>
          <div className="button-group">
            <button className="btn-primary" onClick={() => onEdit(building)}>
              Edit
            </button>
            <button className="btn-secondary" onClick={() => handleDelete(building.id)}>
              Delete
            </button>
          </div>
          <div className="status-temperature">
            <button
              className={`btn-toggle ${building.status === 'Active' ? 'active' : ''}`}
              onClick={() => handleStatusUpdate(building, 'Active')}
            >
              Active
            </button>
            <button
              className={`btn-toggle ${building.status === 'Inactive' ? 'inactive' : ''}`}
              onClick={() => handleStatusUpdate(building, 'Inactive')}
            >
              Inactive
            </button>
            <label>
              New Temperature:
              <input
                type="number"
                value={temperatureInputs[building.id] || ''}
                onChange={(e) =>
                  setTemperatureInputs((prev) => ({ ...prev, [building.id]: e.target.value }))
                }
                placeholder="Set Temp"
              />
            </label>
            <button className="btn-update" onClick={() => handleTemperatureUpdate(building)}>
              Update Temp
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default BuildingList;
