import React from 'react';
import { updateBuilding } from '../services/api';
import { Building } from '../types/types';

interface BuildingListProps {
  buildings: Building[];
  setBuildings: React.Dispatch<React.SetStateAction<Building[]>>;
}

const BuildingList: React.FC<BuildingListProps> = ({ buildings, setBuildings }) => {
  const handleTemperatureUpdate = async (id: number, newTemperature: number) => {
    const buildingToUpdate = buildings.find((b) => b.id === id);
    if (buildingToUpdate) {
      const updatedBuilding = { ...buildingToUpdate, temperature: newTemperature };
      await updateBuilding(id, updatedBuilding);
      setBuildings((prev) =>
        prev.map((b) => (b.id === id ? updatedBuilding : b))
      );
    }
  };

  return (
    <div className="building-list">
      {buildings.map((building) => (
        <div key={building.id} className="building-card">
          <h3>{building.name}</h3>
          <p>Location: {building.location}</p>
          <p>Status: {building.status}</p>
          <p>Current Temperature: {building.temperature}Â°C</p>
          <div className="temperature-controls">
            <label>
              New Temperature:
              <input
                type="number"
                defaultValue={building.temperature}
                onBlur={(e) => handleTemperatureUpdate(building.id, Number(e.target.value))}
              />
            </label>
          </div>
        </div>
      ))}
    </div>
  );
};

export default BuildingList;
