import React, { useEffect } from 'react';
import { fetchBuildings, deleteBuilding } from '../services/api';
import { Building } from '../types/types';
import './BuildingList.css';

interface BuildingListProps {
  buildings: Building[];
  setBuildings: React.Dispatch<React.SetStateAction<Building[]>>;
  onEdit: (building: Building) => void;
}

const BuildingList: React.FC<BuildingListProps> = ({ buildings, setBuildings, onEdit }) => {
  useEffect(() => {
    const loadBuildings = async () => {
      const data = await fetchBuildings();
      setBuildings(data);
    };
    loadBuildings();
  }, [setBuildings]);

  const handleDelete = async (id: number) => {
    await deleteBuilding(id);
    setBuildings(buildings.filter((building) => building.id !== id));
  };

  return (
    <div className="building-list">
      <h2>Buildings</h2>
      <ul>
        {buildings.map((building) => (
          <li key={building.id} className="building-card">
            <h3>{building.name}</h3>
            <p><strong>Temperature:</strong> {building.temperature}Â°C</p>
            <p><strong>Location:</strong> {building.location}</p>
            <p><strong>Status:</strong> {building.status}</p>
            <button className="btn-secondary" onClick={() => onEdit(building)}>Edit</button>
            <button className="btn-danger" onClick={() => handleDelete(building.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BuildingList;
